import React, { useState } from 'react';

const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];

// const names = DUMMY_MEALS[1].name;
// const price = DUMMY_MEALS[1].price;

let names = '';
let price = '';

for (let each of DUMMY_MEALS){
    names += each.name;
    price += each.price
};

const testContext = React.createContext({
    // state: false,
    name: names,
    price: price,
})

export function CartProvider(props){
// const [state, setState ] = useState(false);

//  const name = DUMMY_MEALS.map(meal => {meal.name});
//  const price = () => {DUMMY_MEALS.map(meal => {meal.price})};

return (
    <testContext.Provider
        value={{
        //    state: state,
           name: names,
           price: price,
    }}
    >
        {props.children}
    </testContext.Provider>
);
}

export default testContext;
