            //     existingItem[0].amount += action.list.amount;
            //     return ([...state]);
            // }
            return ([...state, {
                name: action.list.name,
                price: +action.list.price,
                amount: +action.list.amount,
                // totalAmount: totalAmount += action.list.price
            }]);

        }
