import Fragment, { useState,useContext } from 'react';
import MealCart from './MealCart';
import classes from './MealItem.module.css';
import testContext from '../CartProvider';

function MealItem(props) {

    const list = {
        name: props.name,
        price: props.price
    }

    const [lists, setList] = useState('');

    const addtoCart = (item) => {
        setList((prev) => {
            // const n=  [prev];
            // n.unshift(item)
            // console.log(prev);
            const cartlist = [(item), ...prev];
            console.log(cartlist)
            return (cartlist);
        })
        // for (let l of listss) {
        //     const carts = {
        //         names: l.name,
        //         price: l.price,
        //         amount: l.amount
        //     }
        //     console.log(carts);
        // }
    };
    console.log(lists)
// const ctx = useContext(testContext);
    return (
        // <Fragment>
        // <testContext.Provider value={{
        //     name: lists[0]
        // }}/>
        <li className={classes.meal} lists={props.lists}>
            <div className={classes.blocklist}>
                <div>
                    <h2>{props.name}</h2>
                    <p className={classes.desc}>{props.description}</p>
                    <p className={classes.price}>{`$${props.price}`}</p>
                </div>
                <div className={classes.mealcart}>
                    {lists[0]}
                    <MealCart
                        amount={props.value}
                        onClicked={props.onClick}
                        onSave={addtoCart}
                        list={list}
                    />
                </div>
            </div>
        </li>
        // <testContext.Provider />
        // </Fragment>
    )
}
export default MealItem;
